import { cartConstants } from '../constants';

const initialState = {};

export function userAuth(state = initialState, action) {
    switch (action.type) {
        case cartConstants.LOGIN_REQUEST:
            return {
                ...state,
                loading: true
            };
        case cartConstants.LOGIN_SUCCESS:
            return {
                ...state,
                loading: false,
                loggedIn: true,
                user: action.user
            };
        case cartConstants.LOGIN_FAILURE:
            return {};
        case cartConstants.LOGOUT:
            return {};
        case cartConstants.REGISTER_REQUEST:
            return {
                    ...state,
                    loading: true
                };
        case cartConstants.REGISTER_SUCCESS:
            return {
                   ...state,
                   loading: false,
                   loggedIn: true,
                   user: action.user
                };
        case cartConstants.REGISTER_FAILURE:
            return {}; 
        default:
            return state
    }
}
