import { sellerOrderConstants } from '../constants';
import { alertActions } from '.';
import { sellerOrderService } from '../../services';

export const sellerProductActions = {
    orderHistory,
    orderStatus

};

function orderHistory(data, sellerId, setProgress) {
    return dispatch => {

        dispatch(request());

        return sellerOrderService.add(data, sellerId, setProgress)
            .then(
                res => {
                    dispatch(success(res.products));
                    dispatch(alertActions.success("product added successfully"));

                },
                error => {
                    dispatch(failure(error.toString()));
                    dispatch(alertActions.error(error.toString()));
                }
            );
    };

    function request() { return { type: sellerOrderConstants.ADD_REQUEST } }
    function success(products) { return { type: sellerOrderConstants.ADD_SUCCESS, products } }
    function failure(error) { return { type: sellerOrderConstants.ADD_FAILURE, error } }
}

function orderStatus(sellerId) {
    return dispatch => {

        dispatch(request());

        sellerOrderService.view(sellerId)
            .then(
                products => {
                    dispatch(success(products));
                },
                error => {

                    dispatch(failure(error.toString()));
                    dispatch(alertActions.error(error.message.toString()));
                }
            );
    };

    function request() { return { type: sellerOrderConstants.VIEW_REQUEST } }
    function success(products) { return { type: sellerOrderConstants.VIEW_SUCCESS, products } }
    function failure(error) { return { type: sellerOrderConstants.VIEW_FAILURE, error } }
}
