import { sellerConstants } from '../constants';

const initialState = {};

export function sellerProducts(state = initialState, action) {
    switch (action.type) {
        case sellerConstants.ADD_REQUEST:
            return {
                ...state,
                loading: true
            };
        case sellerConstants.ADD_SUCCESS:
            return {
                ...state,
                loading: false,
                products: action.products
            };
        case sellerConstants.ADD_FAILURE:
            return {
                ...state,
                loading: false,
                products: []
            };
        case sellerConstants.REMOVE_REQUEST:
            return {
                ...state,
                loading: true
            };
        case sellerConstants.REMOVE_SUCCESS:
            return {
                ...state,
                loading: false,
                products: action.products
            };
        case sellerConstants.REMOVE_FAILURE:
            return {};
        case sellerConstants.VIEW_REQUEST:
            return {
                ...state,
                loading: true
            };
        case sellerConstants.VIEW_SUCCESS:
            return {
                ...state,
                loading: false,
                products: action.products
            };
        case sellerConstants.VIEW_FAILURE:
            return {};
        default:
            return state
    }
}
