import React, {useState} from 'react'
import { Col, Button, Modal, Form, ProgressBar } from 'react-bootstrap';

function ProductUpload(props) {

    const [productName, setProductName] = useState("");
    const [category, setCategory] = useState("");
    const [color, setColor] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState("");
    const [quantity, setQuanity] = useState("");
    const [productImages, setProductImages] = useState([]);



    const hideModal = () => {
        props.closeModal();
    }

    const handleProductSubmit = () => {

    }

    const imageFileHandler = (e) => {
        setProductImages(e.target.files);
        // const fd = new FormData();
        // fd.append('image', productImages);
        // console.log('fd' + fd);
    }


    const createImageArray = () => {

        let imageArray = [];

        for(let i=0; i<productImages.length; i++) {
            let url = window.URL.createObjectURL(productImages[i]);
            imageArray.push(<img src={url} width={100} height={100} className="mx-2" alt="..." />);
        }

        return imageArray;
    }





    return (
        <Modal
        show={props.showModal}
        onHide={hideModal}
        size="lg"
        backdrop="static"
    >
        <Modal.Header closeButton>
            <Modal.Title>Add product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <ProgressBar className="mb-3" striped variant="success" now={40} />
            <Form onSubmit={handleProductSubmit}>
                <Form.Row>
                    <Form.Group as={Col} md={6} controlId="formGridName">
                        <Form.Label>Product Name</Form.Label>
                        <Form.Control
                            autoFocus
                            type="text"
                            value={productName}
                            placeholder="Product full name"
                            // className={'form-control' + (!sellerNameValidation() && ' is-invalid')}
                            onChange={(e) => setProductName(e.target.value)}
                        />
                        {/* {!sellerNameValidation() &&
                            <div className="invalid-feedback">Name is required</div>
                        } */}

                    </Form.Group>
                    <Form.Group as={Col} md={6} controlId="formGridCompanyName">
                        <Form.Label>Category</Form.Label>
                        <Form.Control
                            type="text"
                            value={category}
                            placeholder="Product category like gadgets, cosmetics etc"
                            // className={'form-control' + (!companyNameValidation() && ' is-invalid')}
                            onChange={(e) => setCategory(e.target.value)}
                        />
                        {/* {!companyNameValidation() &&
                            <div className="invalid-feedback">Company Name is required</div>
                        } */}

                    </Form.Group>
                </Form.Row>

                <Form.Row>
                    <Form.Group as={Col} md={12} controlId="formGridName">
                        <Form.Label>Product description</Form.Label>
                        <Form.Control
                            type="text"
                            value={description}
                            placeholder="About this product"
                            // className={'form-control' + (!sellerNameValidation() && ' is-invalid')}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                        {/* {!sellerNameValidation() &&
                            <div className="invalid-feedback">Name is required</div>
                        } */}

                    </Form.Group>
                </Form.Row>

                <Form.Row>
                    <Form.Group as={Col} md={4} controlId="formGridName">
                        <Form.Label>Price</Form.Label>
                        <Form.Control
                            type="text"
                            value={price}
                            placeholder="Product price in INR"
                            // className={'form-control' + (!sellerNameValidation() && ' is-invalid')}
                            onChange={(e) => setPrice(e.target.value)}
                        />
                        {/* {!sellerNameValidation() &&
                            <div className="invalid-feedback">Name is required</div>
                        } */}

                    </Form.Group>
                    <Form.Group as={Col} md={4} controlId="formGridCompanyName">
                        <Form.Label>quantity</Form.Label>
                        <Form.Control
                            type="number"
                            value={quantity}
                            placeholder="No of products you currently have"
                            // className={'form-control' + (!companyNameValidation() && ' is-invalid')}
                            onChange={(e) => setQuanity(e.target.value)}
                        />
                        {/* {!companyNameValidation() &&
                            <div className="invalid-feedback">Company Name is required</div>
                        } */}

                    </Form.Group>
                    <Form.Group as={Col} md={4} controlId="formGridName">
                        <Form.Label>Product Color</Form.Label>
                        <Form.Control
                            type="text"
                            value={color}
                            placeholder="eg black, blue etc"
                            // className={'form-control' + (!sellerNameValidation() && ' is-invalid')}
                            onChange={(e) => setColor(e.target.value)}
                        />
                        {/* {!sellerNameValidation() &&
                            <div className="invalid-feedback">Name is required</div>
                        } */}

                    </Form.Group>
                </Form.Row>

            </Form>
            <input ref={fileInput => this.fileInput = fileInput} className="d-none" type="file" multiple onChange={imageFileHandler} />
            <Form.Label>Product images: </Form.Label>
            <div onClick={this.fileInput.click()} className="d-flex rounded align-items-center justify-content-center" style={{backgroundColor: "rgba(112, 112, 112, 0.1)", height: 200}}>
             Select files to upload
            </div>
            {productImages.length > 0 && (
            <div className="p-3 mt-2 rounded" style={{backgroundColor: "rgba(112, 112, 112, 0.1)"}}>
            {createImageArray()}
            </div>
            )
            }
        </Modal.Body>
        <Modal.Footer>
            <Button variant="secondary" onClick={hideModal}>Close</Button>
            <Button variant="primary">Add product</Button>
        </Modal.Footer>
    </Modal>
    )
}

export default ProductUpload
