import React, { useState } from 'react'
import './CardPayment.css'


function CardPayment() {

    const [cardType, setCardType] = useState("Credit Card");
    const [cardNumber, setCardNumber] = useState("");
    const [expiryDate, setExpiryDate] = useState("");



    function clearNumber(value = '') {
        return value.replace(/\D+/g, '')
      }


   const formatCreditCardNumber = (value) => {

    const clearValue = clearNumber(value)
    let nextValue;
    nextValue = `${clearValue.slice(0, 4)} ${clearValue.slice(4,8)} ${clearValue.slice(8, 12)} ${clearValue.slice(12, 16)}`;
    setCardNumber(nextValue.trim());

   }

   const formatExpirationDate = (value) => {
    let clearValue = clearNumber(value);


    if (clearValue.length >= 3) {
        clearValue = `${clearValue.slice(0, 2)}/${clearValue.slice(2, 4)}`
    }
    setExpiryDate(clearValue);
  }




    return (
        <div className="container-fluid">
            <h3 style={{ textAlign: "center", marginTop: 20 }}>Pay with {cardType}</h3>
            <hr />
            <div className="container" style={{ borderWidth: 2, borderColor: "black" }}>
                <div className="row">
                    <div className="col-md-12">
                        <label htmlFor="cardType" className="form-label p-2">card Type:</label>
                        <select className="form-select" id="cardType" defaultValue={cardType} onChange={(e) => setCardType(e.target.value)} required>
                            <option value="Credit Card">Credit Card</option>
                            <option value="Debit Card">Debit Card</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <label htmlFor="cardNumber" className="form-label">Credit Card Number: </label>
                        <input type="text" className="form-control" id="cardNumber" value={cardNumber} onChange={(e) => formatCreditCardNumber(e.target.value)}/>
                    </div>
                    <div className="col-md-6">
                        <label htmlFor="cardName" className="form-label">Name on Card: </label>
                        <input type="text" className="form-control" id="cardName" />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-6">
                        <label htmlFor="cvvNumber" className="form-label">Cvv: </label>
                        <input type="text" maxLength={3} pattern="\d{3,4}" className="form-control" id="cvvNumber" />
                    </div>
                </div> 
                <div className="row">
                    <div className="col-md-6">
                        <label htmlFor="Expiry" className="form-label">Expiry Date: </label>
                        <input type="text" pattern="\d\d/\d\d" className="form-control" value={expiryDate} id="Expiry" onChange={(e) => formatExpirationDate(e.target.value)} />
                    </div>
                </div> 
            </div>
        </div>
    )
}

export default CardPayment
