import React, { useState } from 'react';
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css';
import { Row, Col, Form } from 'react-bootstrap';
import { formValidation as validate } from '../../services';

export default function PaymentForm() {

  const [cvc, setCvc] = useState("");
  const [expiry, setExpiry] = useState("");
  const [focus, setFocus] = useState("");
  const [name, setName] = useState("");
  const [number, setNumber] = useState("");


  const handleInputFocus = (e) => {
    setFocus(e.target.name);
  }

  const formatCreditCardNumber = (value) => {
    setNumber(validate.formatCreditCardNumber(value));
  }

  const formatExpirationDate = (value) => {
    setExpiry(validate.formatExpirationDate(value));

  }

  const formatCvc = (value) => {
    setCvc(validate.formatCvc(value));

  }

    return (
      <Row id="PaymentForm">
        <Col>
          <Cards
            cvc={cvc}
            expiry={expiry}
            focused={focus}
            name={name}
            number={number}
          />
        </Col>
        <Col>
          <Form>
            <Form.Row>
              <Form.Group as={Col} md={12}>
                <Form.Control
                  type="text"
                  name="number"
                  value={number}
                  placeholder="Card Number"
                  onChange={(e) => formatCreditCardNumber(e.target.value)}
                  onFocus={handleInputFocus}
                />
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} md={12}>
                <Form.Control
                  type="text"
                  name="name"
                  maxLength={24}
                  value={name}
                  placeholder="Name"
                  onChange={(e) => setName(e.target.value)}
                  onFocus={handleInputFocus}
                />
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} md={6}>
                <Form.Control
                  type="text"
                  name="expiry"
                  value={expiry}
                  placeholder="Valid Thru"
                  onChange={(e) => formatExpirationDate(e.target.value)}
                  onFocus={handleInputFocus}
                />
              </Form.Group>
              <Form.Group as={Col} md={6}>
                <Form.Control
                  type="text"
                  name="cvc"
                  max={3}
                  placeholder="CVC"
                  value={cvc}
                  onChange={(e) => formatCvc(e.target.value)}
                  onFocus={handleInputFocus}
                />

              </Form.Group>
            </Form.Row>
          </Form>
        </Col>
      </Row>
    );
  }
