import React, { useState } from 'react'
import { Container } from 'react-bootstrap'
import './SellerPage.css';
import Sidebar from '../../components/Sidebar';
import SellerNavBar from '../../components/sellerNavBar';
import SellerPrevOrders from '../SellerPrevOrders/SellerPrevOrders';
import SellerDashboard from '../SellerDashboard/SellerDashboard';
import { Route, Switch } from "react-router-dom";



function SellerPage() {

    const [isDarkTheme, setIsDarkTheme] = useState(false);
    const [selectedMenu, setSelectedMenu] = useState("/seller/dashboard");

    const handleTheme = () => {
        setIsDarkTheme(!isDarkTheme);
    }

    const bgColor = isDarkTheme ? "#121212" : "#EEEEEE"

    const handleMenuChange = (value) => {
        setSelectedMenu(value);
    }


    return (
        <div class="d-flex flex-row min-vh-100" >
            <div class="min-vh-100 d-xl-flex d-none flex-shrink-0" style={{ flex: 1, flexDirection: "column" }}>
                <Sidebar />
            </div>
            <div class="dashboard d-flex flex-shrink-1" style={{ flexDirection: "column", backgroundColor: bgColor }}>
                <SellerNavBar selectedMenu={handleMenuChange} />
                <Container className="mt-xl-5" style={{ flex: 1 }}>
                    <div>
                        {selectedMenu === "/seller/dashboard" ? (
                            <SellerDashboard handleTheme={handleTheme} isdarkTheme={isDarkTheme} />
                        ) : (
                            <SellerPrevOrders handleTheme={handleTheme} isdarkTheme={isDarkTheme} />
                        )}
                    </div>

                    {/* <Switch>
                    <Route path="/seller/dashboard" render={() => <SellerDashboard handleTheme={handleTheme} isdarkTheme={isDarkTheme} />} />
                    <Route path="/seller/previousorders" render={() => <SellerPrevOrders handleTheme={handleTheme} isdarkTheme={isDarkTheme} />} />
                </Switch> */}

                </Container>
            </div>
        </div>
    )
}

export default SellerPage
