import React, {useState} from 'react'
import { Card, ListGroup, ListGroupItem, Button, Carousel, Image, Modal } from 'react-bootstrap';

function SellerProductCard(props) {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);


  const handleQtyClick = () => {
    handleShow();
  }



  return (
    <div>
     <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
      >
        <Modal.Header closeButton>
          <Modal.Title>Add Quantity</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          I will not close if you click outside me. Don't even try to press
          escape key.
        </Modal.Body>
        <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary">Add</Button>
        </Modal.Footer>
      </Modal>
      <Card style={{ width: '20rem', marginBottom: 30 }}>
        <Carousel interval={null}>
        {props.images && props.images.length !== 0 && (
          props.images.map((item) => (
            <Carousel.Item>
              <Image
                src={item.fileUrl}
                alt="First slide"
                style={{height: 280, width: '100%'}}

              />
            </Carousel.Item>
                      ))
                    )}
          </Carousel>
        <Card.Body>
          <Card.Title>{props.productName}</Card.Title>
          <Card.Text>
          {props.descr}
          </Card.Text>
        </Card.Body>
        <ListGroup className="list-group-flush">
          <ListGroupItem>Price: â‚¹{props.price}</ListGroupItem>
          <ListGroupItem>Available quantity: {props.quantity}</ListGroupItem>
        </ListGroup>
        <Card.Body>
        <Button className="mr-3" variant="danger">Delete Product</Button>
        <Button variant="dark" onClick={() => handleQtyClick()}>Add Quantity</Button>
        </Card.Body>
      </Card>
    </div>
  )
}

export default SellerProductCard
