import React, { useState, useCallback } from 'react';
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css';
import { Row, Col, Form } from 'react-bootstrap';
import { formValidation as validate } from '../../services';

export default function PaymentForm({cvc, setCvc, expiry, setExpiry, name, setName, number, setNumber}) {


  const [focus, setFocus] = useState("");

  const handleInputFocus = (e) => {
    setFocus(e.target.name);
  }

  const handleNameChange = useCallback(value => {
    setName(value)
  }, [setName]);

  const handleNumberChange = useCallback(value => {
    setNumber(validate.formatCreditCardNumber(value));
  }, [setNumber]);

  const handleExpiryChange = useCallback(value => {
    setExpiry(validate.formatExpirationDate(value));
  }, [setExpiry]);

  const handleCVCChange = useCallback(value => {
    setCvc(validate.formatCvc(value));
  }, [setCvc]);


    return (
      <Row id="PaymentForm">
        <Col>
          <Cards
            cvc={cvc}
            expiry={expiry}
            focused={focus}
            name={name}
            number={number}
          />
        </Col>
        <Col>
          <Form>
            <Form.Row>
              <Form.Group as={Col} md={12}>
                <Form.Control
                  type="text"
                  name="number"
                  value={number}
                  placeholder="Card Number"
                  onChange={(e) => handleNumberChange(e.target.value)}
                  onFocus={handleInputFocus}
                />
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} md={12}>
                <Form.Control
                  type="text"
                  name="name"
                  maxLength={24}
                  value={name}
                  placeholder="Name"
                  onChange={(e) => handleNameChange(e.target.value)}
                  onFocus={handleInputFocus}
                />
              </Form.Group>
            </Form.Row>
            <Form.Row>
              <Form.Group as={Col} md={6}>
                <Form.Control
                  type="text"
                  name="expiry"
                  value={expiry}
                  placeholder="Valid Thru"
                  onChange={(e) => handleExpiryChange(e.target.value)}
                  onFocus={handleInputFocus}
                />
              </Form.Group>
              <Form.Group as={Col} md={6}>
                <Form.Control
                  type="text"
                  name="cvc"
                  max={3}
                  placeholder="CVC"
                  value={cvc}
                  onChange={(e) => handleCVCChange(e.target.value)}
                  onFocus={handleInputFocus}
                />

              </Form.Group>
            </Form.Row>
          </Form>
        </Col>
      </Row>
    );
  }
