// import config from 'config';
const axios = require('axios');
const baseURL = "http://localhost:8080"

export const homeService = {
    viewCategory,
};



function viewCategory(categories) {


    const requestOne = axios.get(`${baseURL}/product/home?category=${categories[0]}`);
    const requestTwo = axios.get(`${baseURL}/product/home?category=${categories[1]}`);
    const requestThree = axios.get(`${baseURL}/product/home?category=${categories[2]}`);



    return axios.all([requestOne, requestTwo, requestThree])
        .then(response => {
            return response.data;
        })
        .catch(err => {
            return Promise.reject(err.response.data.message);
        })

}
