import React, { useState, useEffect, useRef } from 'react'
import { AnimatePresence, motion } from "framer-motion";
import { useClickOutside } from "react-click-outside-hook";
import { BiSearch } from "react-icons/bi";
import { AiFillCloseCircle } from "react-icons/ai";
import { Spinner, Form } from "react-bootstrap";
import './SearchBar.css';


function SearchBar() {

    const [isExpanded, setExpanded] = useState(false);
    const [parentRef, isClickedOutside] = useClickOutside();
    const inputRef = useRef();
    const [searchQuery, setSearchQuery] = useState("");
    const [isLoading, setLoading] = useState(false);
    const [tvShows, setTvShows] = useState([]);
    const [noTvShows, setNoTvShows] = useState(false);

    const isEmpty = !tvShows || tvShows.length === 0;

    const changeHandler = (e) => {
        e.preventDefault();
        if (e.target.value.trim() === "") setNoTvShows(false);

        setSearchQuery(e.target.value);
    };

    const expandContainer = () => {
        setExpanded(true);
    };

    const collapseContainer = () => {
        setExpanded(false);
        setSearchQuery("");
        setLoading(false);
        setNoTvShows(false);
        setTvShows([]);
        if (inputRef.current) inputRef.current.value = "";
    };

    useEffect(() => {
        if (isClickedOutside) collapseContainer();
    }, [isClickedOutside]);

    const containerVariants = {
        expanded: {
            height: "30em",
            position: "absolute",
            top: 0,
            left: 0
        },
        collapsed: {
            height: "3.8em",
            position: "absolute",
            top: 0,
            left: 0
        },
    };

    const containerTransition = { type: "spring" , damping: 22, stiffness: 150 };




    return (
        <motion.div
            className="searchBarContainer"
            animate={isExpanded ? "expanded" : "collapsed"}
            variants={containerVariants}
            transition={containerTransition}
            ref={parentRef}
        >
            <div className="SearchInputContainer">
                <BiSearch size={28} color="#bebebe" className="mr-1" />
                <input
                    className="searchInput"
                    style={{ width: '100%', height: '100%', borderWidth: 0, outline: 0, color: "black", backgroundColor: "transparent", }}
                    type="text"
                    placeholder="Search for products"
                    onFocus={expandContainer}
                    ref={inputRef}
                    value={searchQuery}
                    onChange={changeHandler}
                />
                <AnimatePresence>
                    {isExpanded && (
                        <motion.span
                            className="CloseIcon"
                            whileHover={{ scale: 1.1, color: "#bebebe" }}
                            key="close-icon"
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            onClick={collapseContainer}
                            transition={{ duration: 0.2 }}
                        >
                            <AiFillCloseCircle size={22} />
                        </motion.span>
                    )}
                </AnimatePresence>
            </div>
            {isExpanded && <span className="LineSeperator" />}

            {isExpanded && (
                <div className="SearchContent">
                    {isLoading && (
                        <div className="LoadingWrapper">
                            <Spinner animation="grow" color="#000" size={20} />
                        </div>
                    )}
                    {!isLoading && isEmpty && !noTvShows && (
                        <div className="LoadingWrapper">
                            <span className="WarningMessage">Start typing to Search</span>
                        </div>
                    )}
                    {!isLoading && noTvShows && (
                        <div className="LoadingWrapper">
                            <span className="WarningMessage">No Tv Shows or Series found!</span>
                        </div>
                    )}
                    {!isLoading && !isEmpty && (
                        <>
                            {tvShows.map(({ show }) => (
                                <p>hiii</p>
                            ))}
                        </>
                    )}
                </div>
            )}


        </motion.div>
    )
}

export default SearchBar
