import React from 'react';
import { Navbar, Nav, NavDropdown, Form, FormControl, Button } from 'react-bootstrap';
import Logo from '../../images/logo.svg';
import { useDispatch } from 'react-redux';
import { userActions } from '../../redux/actions';

function NavBar(props) {

  const dispatch = useDispatch();

  const handleLogout = () => {

    dispatch(userActions.logout()); 

  }



    return (
        <Navbar collapseOnSelect bg="light" expand="lg" fixed="top" className="px-md-5 shadow-sm mb-5 rounded">
        <Navbar.Brand href="/">
        <img
        src={Logo}
        width="200"
        height="60"
        className="d-inline-block align-top"
        alt="capricon logo"
      />
        </Navbar.Brand>

        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Form inline>
            <FormControl type="text" placeholder="Search" className="mr-sm-2" />
            <Button variant="outline-success">Search</Button>
          </Form>
          <Nav className="ml-auto">
           {props.loggedIn ? (
            <NavDropdown title={props.name} id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.1">My Profile</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">My Orders</NavDropdown.Item>
              <NavDropdown.Item onClick={() => handleLogout()}>Log out</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.4">Sell on Capricon</NavDropdown.Item>
            </NavDropdown>
           ) : (
            <Button href="/login" className="px-5" variant="primary">Login</Button>
           )}

            <Nav.Link href="/Login" className="px-5">Wishlist</Nav.Link>
            <Nav.Link href="/Cart">Cart</Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Navbar>

    )
}

export default NavBar
