import { sellerConstants } from '../constants';
import { alertActions } from './';
import { sellerService } from '../../services';

export const sellerActions = {
    addProduct,
    viewProduct
};

function addProduct(userId, productId, history) {
    return dispatch => {

        sellerService.addProduct(userId, productId)
            .then(
                cartItems => { 
                        dispatch(success(cartItems));
                        history.push('/cart');

                },
                error => {
                    dispatch(failure(error.toString()));
                    dispatch(alertActions.error(error.toString()));
                }
            );
    };

    function success(cartItems) { return { type: sellerConstants.ADD_SUCCESS, cartItems } }
    function failure(error) { return { type: sellerConstants.ADD_FAILURE, error } }
}

function viewProduct(userId) {
    return dispatch => {

        addProduct.view(userId)
            .then(
                cartItems => { 
                    dispatch(success(cartItems));
                },
                error => {
                    if(error.httpStatusCode !== 404){
                        dispatch(failure(error.toString()));
                        dispatch(alertActions.error(error.message.toString()));
                    } else {
                        dispatch(success([]));
                    }

                }
            );
    };

    function success(cartItems) { return { type: sellerConstants.VIEW_SUCCESS, cartItems } }
    function failure(error) { return { type: sellerConstants.VIEW_FAILURE, error } }
}
